mapper.select:
SELECT * FROM article

	where boardId = #{boardId}		

ORDER BY id DESC
limit #{limitStart}, #{limitTake}

Select * 
from article

	
		where title = #{title}
	
	
		where body = #{body}

mapper.update:
update article 

	
		
			title = #{title}
		
		
			body = #{body}	
		
		
			title = #{title},
			body = #{body}
		
	

where id = #{id}
